import React from 'react';
import { Link, useLocation } from "react-router-dom";
import './Nav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Nav = () => {
  const iconColors = {
    natLight: "#ffb457",
    natDark: "#f88e0b",
    csLight: "#ff96bd",
    csDark: "#f85792",
    musicLight: "#9999fb",
    musicDark: "#5f5fff",
    comedyLight: "#59d5e8",
    comedyDark: "#01b7d3"
  }
  let natIconColor = {
    color: iconColors.natDark
  };
  let csIconColor = {
    color: iconColors.csLight
  };
  let musicIconColor = {
    color: iconColors.musicLight
  };
  let comedyIconColor = {
    color: iconColors.comedyLight
  };
  let path = useLocation().pathname;

  switch(path) {
    case "/nat":
      natIconColor = iconColors.natDark;
      csIconColor = iconColors.csLight;
      musicIconColor = iconColors.musicLight;
      comedyIconColor = iconColors.comedyLight;
      break;
    case "/cs":
      natIconColor = iconColors.natLight;
      csIconColor = iconColors.csDark;
      musicIconColor = iconColors.musicLight;
      comedyIconColor = iconColors.comedyLight;
      break;
    case "/music":
      natIconColor = iconColors.natLight;
      csIconColor = iconColors.csLight;
      musicIconColor = iconColors.musicDark;
      comedyIconColor = iconColors.comedyLight;
      break;
    case "/comedy":
      natIconColor = iconColors.natLight;
      csIconColor = iconColors.csLight;
      musicIconColor = iconColors.musicLight;
      comedyIconColor = iconColors.comedyDark;
      break;
    default:
      natIconColor = iconColors.natDark;
      csIconColor = iconColors.csLight;
      musicIconColor = iconColors.musicLight;
      comedyIconColor = iconColors.comedyLight;
  }
  /*
  console.log(background);
  const myBackground = {
    backgroundColor: background
  }*/

  return (
    <div className="nav" >
      <nav>
        <ul className="tab-row">
            <li>
                <Link to="/nat" className="tab" id="tab1">
                  <FontAwesomeIcon icon="hand" />
                </Link>
            </li>
            <li>
                <Link to="/cs" className="tab" id="tab2">
                  <FontAwesomeIcon icon="computer" />
                </Link>
            </li>
            <li>
                <Link to="/music" className="tab" id="tab3">
                  <FontAwesomeIcon icon="music" />
                </Link>
            </li>
            <li>
                <Link to="/comedy" className="tab" id="tab4">
                  <FontAwesomeIcon icon="masks-theater" />
                </Link>
            </li>
        </ul>
      </nav>
    </div>

  )
}

export default Nav;
